import React, { Component } from 'react'

class TransactionForm extends Component {
    state = {
      bAccountNo : '',
      iFSC : '',
      bName : '',
      amount : ''
    }

    handleInputChange = e => {
     this.setState({
           [e.target.name] : e.target.value
     })
    }

    render() {
        return (
            <form autoComplete="off">
                <input name="bAccuntNo" placeholder="A/C No." value={this.state.bAccountNo} onChange={this.handleInputChange} />
            <div>{this.state.name}</div>
            </form>
        )
    }
}

export default TransactionForm
